mapper.select:
select ( case when deptname = '分包方-保安' or deptname = '分包方-保安' or deptname = '分包方-饭堂' or deptname = '分包方-清洁' THEN 1 else 0 END) deptname,(count(CASE WHEN reader_name like 'TKI%' THEN 1 END )-count(CASE WHEN reader_name like 'TKO%' THEN 1 END ))n from acclog_view where 1=1 and deptname not in ('省装','试验塔','调试临时卡组', 'Yangjiang Office','test')
and time > #{startTime}
GROUP BY pin,deptname having (count(CASE WHEN reader_name like 'TKI%' THEN 1 END )-count(CASE WHEN reader_name like 'TKO%' THEN 1 END )) != 0

select pin,name,mobile,deptname,(case when type = 0 then '内部员工' when type = 1 then '长期供应商' end) type,n, (case when n = 1 then '在园区' when n = 0 then '已签离' when n >1 or n < 0 then '异常' end) s from ( select pin,name,mobile,deptname,( case when deptname = '分包方-保安' or deptname = '分包方-保安' or deptname = '分包方-饭堂' or deptname = '分包方-清洁' THEN 1 else 0 END) type, (count(CASE WHEN reader_name like 'TKI%' THEN 1 END )-count(CASE WHEN reader_name like 'TKO%' THEN 1 END ))n from acclog_view where 1=1 and deptname not in ('省装','试验塔','调试临时卡组', 'Yangjiang Office','test') and event_type = '正常验证开门'
and time > #{startTime}
and time < #{endTime}
and time not in (${times})
GROUP BY pin,name,mobile,deptname having (count(CASE WHEN reader_name like 'TKI%' THEN 1 END )-count(CASE WHEN reader_name like 'TKO%' THEN 1 END )) != 0 ) a
